lazy_static! {
    static ref PRC_BUF_32: PCRCbuffer<32> = PCRCbuffer::generate();
    static ref PRC_BUF_64: PCRCbuffer<64> = PCRCbuffer::generate();
    static ref PRC_BUF_128: PCRCbuffer<128> = PCRCbuffer::generate();
    static ref PRC_BUF_512: PCRCbuffer<512> = PCRCbuffer::generate();
}

fn pcrc_20x20_bench(b: &mut Criterion) {
    let map = create_map_20x20();

    b.bench_function("PCRC 20x20", move |b| {
         b.iter(|| {
              let mut map = map.clone();
              let radius = map.size().x as usize;
              let player_pos = map.player_pos().unwrap();

              PCRC { area:     map.area(),
                     buffer:   &PRC_BUF_32,
                     callback: TestMap::fov_func,
                     cb_type:  &mut map,
                     radius:   radius,
                     vision:   VisionShape::Octagon, }.fov(player_pos)
          })
     });
}

